  {
    const doc = new GoogleSpreadsheet('10sC4pSRXWgWiQPbaGVfBBRw7qHtk-PSFTJ9XyUvq-QM');

    doc.useServiceAccountAuth({
      client_email: process.env.GOOGLE_SERVICE_ACCOUNT_EMAIL,
      private_key: process.env.GOOGLE_PRIVATE_KEY,
    });

