import './App.css';
import React from 'react';

class Professores extends React.Component
{
  state = {
    professores: [],
    update: false
  };

  updateProfs()
  {
    fetch('test.json',
          {
            mode: 'no-cors',
            headers: {
              'Content-Type': 'application/json',
              'Accept': 'application/json'
            }
          })
    .then((response) =>
      {
        if (response.ok)
        {
          console.log(response);
          return response.json();
        }
      }
    ).then((contents) =>
      {
        if (contents != this.state.professores)
        {
          console.log(contents);
          this.setState({professores: contents, update: true});
        }
      }
    );
  }
  shouldComponentUpdate()
  {
    if (this.state.update)
    {
      this.setState({update:false});
      return true;
    }
    return false;
  }
  render()
  {
    this.updateProfs();
    console.log(this.state.professores);

    return (
      <div>
        <h1>Professores</h1>
        <ol>
          {this.state.professores.map((prof) => (
            <li key={prof.id}>{prof.name}</li>
          ))}
        </ol>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <Professores/>
    </div>
  );
}

export default App;
