import './App.css';
import React from 'react';

class Professores extends React.Component
{
  state = {
    professores: [],
    update: false
  };

  updateProfs()
  {
    fetch('test.json',
          {
            mode: 'no-cors',
            headers: {
              'Content-Type': 'application/json',
              'Accept': 'application/json'
            }
          })
    .then((response) =>
      {
        if (response.ok)
        {
          return response.json();
        }
      })
    .then((contents) =>
      {
        console.log(contents);
        console.log(this.state.professores);
        var updt = (this.state.professores !== contents);
        console.log(updt);
        this.setState({professores: contents, update: updt});
      });
  }
  shouldComponentUpdate()
  {
    var updt = this.state.update;
    this.setState({professores: this.state.professores, update:false});
    return updt;
  }
  render()
  {
    return (
      <div>
        <h1>Professores</h1>
        <ol>
          {this.state.professores.map((prof) => (
            <li key={prof.id}>{prof.name}</li>
          ))}
        </ol>
      </div>
    );
  }
}

function App() {
  Professores.updateProfs();
  return (
    <div className="App">
      <Professores/>
    </div>
  );
}

export default App;
